sys_transform_script.script:
// Assuming dateStr is the value from your source data
var dateStr = "2/4/2024";

// Parse the date
var parsedDate = new Date(dateStr);

// Set the "u_when_needed" field
target.u_when_needed = parsedDate;

// Or if the "u_when_needed" field expects a formatted string
target.u_when_needed = ("0" + (parsedDate.getMonth() + 1)).slice(-2) + "-" +
                      ("0" + parsedDate.getDate()).slice(-2) + "-" +
                      parsedDate.getFullYear() + " " +
                      ("0" + parsedDate.getHours()).slice(-2) + ":" +
                      ("0" + parsedDate.getMinutes()).slice(-2) + ":" +
                      ("0" + parsedDate.getSeconds()).slice(-2);
